import 'package:flutter/material.dart';
import 'package:provider/provider.dart';

class CardProvider extends ChangeNotifier {
  List<String> _urlImages = [];

  bool _isDragging = false;
  double _angle = 0;
  Offset _position = Offset.zero;
  Size _screenSize = Size.zero;
  Offset get position => _position;

  List<String> get urlImages => _urlImages;
  double get angle => _angle;
  bool get isDragging => _isDragging;

  CardProvider() {
    resetUsers();
  }

  void setScreenSize(Size screenSize) => _screenSize = screenSize;

  void startPosition(DragStartDetails details) {
    _isDragging = true;
    notifyListeners();
  }

  void updatePosition(DragUpdateDetails details) {
    _position += details.delta;
    final x = _position.dx;
    _angle = 45 * x / _screenSize.width;
    notifyListeners();
  }

  void endPosition() {
    resetPosition();
  }

  void resetPosition() {
    _isDragging = false;
    _position = Offset.zero;
    _angle = 0;

    notifyListeners();
  }

  void resetUsers() {
    _urlImages = <String>[
      'https://www.google.com/imgres?imgurl=https%3A%2F%2Flastfm.freetls.fastly.net%2Fi%2Fu%2Far0%2Fb160ba38c8272abdc98ff6877274f185.jpg&imgrefurl=https%3A%2F%2Fwww.last.fm%2Fmusic%2FNiall%2BHoran%2F%2Bimages&tbnid=5sycXaxj7_Q4BM&vet=12ahUKEwik4Km94Zr8AhWDKbcAHcCuARAQMygMegUIARD6AQ..i&docid=ge2reojN_j9dZM&w=900&h=900&q=niall%20horan&ved=2ahUKEwik4Km94Zr8AhWDKbcAHcCuARAQMygMegUIARD6AQ',
      'data:image/jpeg;base64,',
      'data:image/jpeg;base64,'
          'data:image/jpeg;base64,'
          'data:image/jpeg;base64,'
    ].reversed.toList();
    notifyListeners();
  }
}
